mapper.select:
select
id, uid, union_id, nick_name, phone, gender, province, city, country, avatar_url, status,
create_time, create_user, update_time, update_user
from usr_user


    and id = #{id}


    and uid = #{uid}


    and union_id = #{unionId}


    and nick_name = #{nickName}


    and phone = #{phone}


    and gender = #{gender}


    and province = #{province}


    and city = #{city}


    and country = #{country}


    and avatar_url = #{avatarUrl}


    and status = #{status}


    and create_time = #{createTime}


    and create_user = #{createUser}


    and update_time = #{updateTime}


    and update_user = #{updateUser}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from usr_user


    and id = #{id}


    and uid = #{uid}


    and union_id = #{unionId}


    and nick_name = #{nickName}


    and phone = #{phone}


    and gender = #{gender}


    and province = #{province}


    and city = #{city}


    and country = #{country}


    and avatar_url = #{avatarUrl}


    and status = #{status}


    and create_time = #{createTime}


    and create_user = #{createUser}


    and update_time = #{updateTime}


    and update_user = #{updateUser}
