mapper.select:
select
  id, app_code, app_id, app_secret, app_platform_code, remarks
from wechat_app_info


        and id = #{id}


        and app_code = #{appCode}


        and app_id = #{appId}


        and app_secret = #{appSecret}


        and app_platform_code = #{appPlatformCode}


        and remarks = #{remarks}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from wechat_app_info


        and id = #{id}


        and app_code = #{appCode}


        and app_id = #{appId}


        and app_secret = #{appSecret}


        and app_platform_code = #{appPlatformCode}


        and remarks = #{remarks}
